          API.saveBook({
            title: props.title,
           
          })
            .then(res => loadBooks())
            .catch(err => console.log(err));
        }
      };

    return (
        <div className='card-container'>
          <a href={props.link} target='_blank'>
          <img src={props.image} alt={props.title} title={props.title}/>
          </a>
                <div className='desc'>
                <h2>{props.title}</h2>
                <h4>{props.subtitle}</h4>
                <h3>{props.author}</h3>
                <h4>Published Date: {props.date}</h4>
                <p>{props.description}</p>
                <a href={props.link} target="_blank">Google Book link to: {props.title}</a> 
                <button onClick={handleGformSubmit} className='button'>Add to List</button>
        </div>
        </div>
    )
}
