import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import API from "../utils/API";

function Gbdetail(props) {
  const [gbook, setGbook] = useState({})

  // When this component mounts, grab the book with the _id of props.match.params.id
  // e.g. localhost:3000/books/599dcb67f0f16317844583fc
  
  const {id} = useParams()
  useEffect(() => {
    API.getGbook(id)
      .then(res => setGbook(res.data))
      .catch(err => console.log(err));
      // eslint-disable-next-line
  }, [])

  return (
      <Container fluid>
       <Row>
          <Col size="md-10 md-offset-1">
          <div className='card-container'>
          <a href={gbook.link} target='_blank' rel="noopener noreferrer">
          <img src={gbook.image} alt={gbook.title} title={gbook.title}/>
          </a>
                <div className='desc'>
                <h2>{gbook.title}</h2>
                <h4>{gbook.subtitle}</h4>
                <h3>{gbook.author}</h3>
                <h4>Published Date: {gbook.date}</h4>
                <p>{gbook.description}</p>
                <a href={gbook.link} target="_blank" rel="noopener noreferrer">Google Book link to: {gbook.title}</a> 
             
        </div>
        
        </div>
            
          </Col>
        </Row>
        <Row>
          <Col size="md-2">
            <Link to="/">‚Üê Back to Authors</Link>
          </Col>
        </Row>
      </Container>
    );
  }


export default Gbdetail;
