import { findByLabelText } from "@testing-library/react";
import React, { Component } from "react";
import Jumbotron from "../components/Jumbotron"
import API from "../utils/request";
import BookList from './BookList'


class Form extends Component {
  // Setting the component's initial state
  state = {
    userInput: '',  
    books: []
  };

  handleInputChange = event => {
    // Getting the value and name of the input which triggered the change
    let value = event.target.value;
    const userInput= event.target.value;
    console.log(value);
    // Updating the input's state
    this.setState({
      userInput: value
    });
  };

  handleFormSubmit = event => {
    // Preventing the default behavior of the form submit (which is to refresh the page)
    event.preventDefault();
    // console.log ("this runs on click")
     this.setState({
      userInput: ""
    });


    API.searchGoogleBooks(this.state.userInput)
     .then ( data => {
       console.log(data.data.items)
       let gbObject = data.data.items
          this.setState({ books: [...gbObject]})
      })}


    //    for (const i of gbObject) {
    //        console.log(i)
    //     let shortBook = { 
    //         title : i.volumeInfo.title, 
    //         subtitle : i.volumeInfo.subtitle,
    //         image : i.imagelinks,
    //         date: i.volumeInfo.publishedDate, 
    //         description : i.volumeInfo.description, 
    //     };
         
    //     concat(state.data) = shortBook
        // console.log(shortBook)
           //push into Data - 
  

  render() {
    const formstyle = {
     display: "flex",
     flexDirection: 'row',
     textAlign: 'center',
     alignSelf: "center",

    }


    return (
      <div>
        <form styleclassName="search-form">
          <input
            name="booktitle"
            onChange={this.handleInputChange}
            type="text"
            placeholder="Enter Title of Book"
          /> 
          <button onClick={this.handleFormSubmit}>SEARCH GOOGLE BOOKS</button>
        </form>
         <h1>Google Book Search Results</h1>
        
         <BookList books={this.state.books} />
      </div>
    );
  }
}

export default Form;
