// import React from 'react'
import './pages.css'
import API from "../utils/API";
import React, { useState, useEffect } from "react";
import { GformBtn } from "../components/Form";

export default function BookCard(props) {
    const [books, setBooks] = useState([])
    // this code copied from books.js    7-24
     function loadBooks() {
        API.getBooks()
          .then(res => 
            setBooks(res.data)
          )
          .catch(err => console.log(err));
      };
     
     
    function handleGformSubmit(event) {
        event.preventDefault();
        // console.log(props)
         API.saveBook({
            title: props.title,
            
          })
          
            .then(res => loadooks())
            
            .catch(err => console.log(err));
     };

    return (
        <div className='card-container'>
          <a href={props.link} target='_blank'>
          <img src={props.image} alt={props.title} title={props.title}/>
          </a>
                <div className='desc'>
                <h2>{props.title}</h2>
                <h4>{props.subtitle}</h4>
                <h3>{props.author}</h3>
                <h4>Published Date: {props.date}</h4>
                <p>{props.description}</p>
                <a href={props.link} target="_blank">Google Book link to: {props.title}</a> 
                <form>
                <GformBtn
                  className='button' onClick={handleGformSubmit} 
                  >
                 Add to List
                </GformBtn>
                </form>
        </div>
        </div>
    )
}
