import React, { Component } from "react";
import Jumbotron from "../components/Jumbotron"
import { searchGoogleBooks } from "../utils/request";
// import "./style.css";

class Form extends Component {
  // Setting the component's initial state
  state = {
    bookTitle: ""    
  };

  handleInputChange = event => {
    // Getting the value and name of the input which triggered the change
    let value = event.target.value;
    const bookTitle = event.target.value;
    // console.log(value);
    // Updating the input's state
    this.setState({
      bookTitle: value
    });
  };

  handleFormSubmit = event => {
    // Preventing the default behavior of the form submit (which is to refresh the page)
    event.preventDefault();
    // console.log ("this runs on click")
    
    this.setState({
      bookTitle: ""
    });
    searchGoogleBooks(this.state.bookTitle)
     .then ( () => {}
     
     )
     
  };

  render() {
    
    return (
      <div>
        <form className="search-form">
          <input
            name="booktitle"
            onChange={this.handleInputChange}
            type="text"
            placeholder="Enter Title of Book"
          /> 
          <button onClick={this.handleFormSubmit}>SEARCH GOOGLE BOOKS</button>
        </form>
        <Jumbotron>
              <h1>Google Book Search Results</h1>
        </Jumbotron>



      </div>
    );
  }
}

export default Form;
