const cleanByKey = (keys) => (obj) =>
  keys.reduce((newObj, key) => {
    newObj[key] = obj[key];
    return newObj;
  }, {});

export const cleanMyBooks = cleanByKey([
  'author',
  'title',
  'subtitle',
  'description',
  'image',
  'link',
]);
